import React from 'react';
import './ProductDetails.css';
import { useParams } from 'react-router-dom';

const ProductDetails = ({ products, onAddToCart }) => {
  const { productId } = useParams();
  const product = products.find((p) => p.id === parseInt(productId));

  if (!product) {
    return <h2>Product not found!</h2>;
  }

  return (
    <div className="productDetails">
      <div className="productDetails__image">
        <img src={product.imageUrl} alt={product.name} />
      </div>
      <div className="productDetails__info">
        <h2>{product.name}</h2>
        <p className="productDetails__price">Price: â‚¹{product.price}</p>
        <button onClick={() => onAddToCart(product)} className="productDetails__addToCartButton">
          Add to Cart
        </button>
        <p className="productDetails__description">
          This is a detailed description of the product. You can add more information here.
        </p>
      </div>
    </div>
  );
};

export default ProductDetails;
