import React from 'react';
import './Cart.css';

const Cart = ({ cartItems = [], onRemoveFromCart }) => {
  const calculateTotal = () => {
    if (!Array.isArray(cartItems)) return 0; // Check if cartItems is an array
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cartItems.map((item) => (
            <div key={item.id} className="cart__item">
              <img src={item.imageUrl} alt={item.name} className="cart__itemImage" />
              <div className="cart__itemDetails">
                <p>{item.name}</p>
                <p>Price: ₹{item.price}</p>
                <button onClick={() => onRemoveFromCart(item.id)} className="cart__removeButton">
                  Remove
                </button>
              </div>
            </div>
          ))}
          <div className="cart__total">
            <h3>Total: ₹{calculateTotal()}</h3>
          </div>
          <button className="cart__checkoutButton">Proceed to Checkout</button>
        </>
      )}
    </div>
  );
};

export default Cart;
