import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header/Header';
import ProductList from './components/ProductList/ProductList';
import ProductDetails from './components/ProductList/ProductDetails';
import Footer from './components/Footer/Footer';

function App() {
  const [cart, setCart] = useState([]);

  const products = [
    { id: 1, name: 'Apple iPhone 14', price: 79999, imageUrl: '' },
    { id: 2, name: 'Samsung Galaxy S22', price: 69999, imageUrl: 'https://images.samsung.com/is/image/samsung/p6pim/in/galaxy-s22' },
    { id: 3, name: 'Sony WH-1000XM4 Headphones', price: 24999, imageUrl: 'https://cdn.shopify.com/s/files/1/2337/1377/products/sony_wh-1000xm4_bluetooth' },
    { id: 4, name: 'Mi TV 4A Pro 32 inch HD Ready', price: 15999, imageUrl: 'https://i01.appmifile.com/webfile/globalimg/in/cms/E2C8AA9A-F19F-F79B-C9EA-23162C859545.jpg' },
    { id: 5, name: 'boAt Airdopes 141 True Wireless Earbuds', price: 1499, imageUrl: 'https://cdn.shopify.com/s/files/1/0057/8938/4802/products/main-img-4_300x.png' },
    { id: 6, name: 'Amazon Echo Pop', price: 3499, imageUrl: 'https://m.media-amazon.com/images/I/41qlHp9bRSL._SY450_.jpg' },
    { id: 7, name: 'Amazon Echo Show 8', price: 10499, imageUrl: 'https://m.media-amazon.com/images/I/61FyR4I9DHL._SX425_.jpg' }
  ];

  const addToCart = (product) => {
    setCart([...cart, product]);
    alert(`${product.name} added to the cart!`);
  };

  return (
    <Router>
      <div className="App">
        <Header />
        <Routes>
          <Route path="/" element={<ProductList />} />
          <Route 
            path="/product/:productId" 
            element={<ProductDetails products={products} onAddToCart={addToCart} />} 
          />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
